    private void handleLogin(String nom, String motDePasse) {
        nom = nom.trim();

        if (nom.isEmpty() || motDePasse.isEmpty()) {
            JOptionPane.showMessageDialog(this,
                    "Veuillez saisir votre nom d'utilisateur et mot de passe",
                    "Champs requis", JOptionPane.WARNING_MESSAGE);
            return;
        }

        // Create an instance of the controller
        AuthentificationController authController = new AuthentificationController();
        String[] loginResult = authController.loginAndGetRole(nom, motDePasse);

        if (loginResult != null) {
            String role = loginResult[1];
            Client currentClient = authController.loginAndGetClient(nom,motDePasse);

            if (currentClient != null) {
                JOptionPane.showMessageDialog(this, "Connexion r√©ussie!");

                // Check role and open appropriate window
                if ("admin".equals(role)) {
                    // Open admin interface
                    GestionCompteIHM adminWindow = new GestionCompteIHM(currentClient);
                    adminWindow.setVisible(true);
                } else {
                    // Open regular user interface
                    CompteIHM mainWindow = new CompteIHM(currentClient);
                    mainWindow.setVisible(true);
                }
                this.dispose(); // Close login window
            }
        } else {
            JOptionPane.showMessageDialog(this,
                    "Nom d'utilisateur ou mot de passe incorrect",
                    "Erreur d'authentification", JOptionPane.ERROR_MESSAGE);
            txtMDP.setText("");
            txtMDP.requestFocus();
        }
    }
    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        txtNom.requestFocus();
    }//GEN-LAST:event_formWindowOpened

    private void btnConnectActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConnectActionPerformed
        String username = txtNom.getText();
        String password = new String(txtMDP.getPassword()); // Convert password to string

        handleLogin(username, password);
    }//GEN-LAST:event_btnConnectActionPerformed

